export interface ReviewTableData {
    id: string;
    name: string;
    ipc: string;
    keywords: string;
    sourceTable: string;
}

const processData = (rawData: string[][], tableName: string): ReviewTableData[] => {
    return rawData.map(([id, name, ipc, keywords]) => ({
        id,
        name,
        ipc,
        // 智能填充：如果关键词为空，自动使用技术分支名称，确保AI有内容可匹配
        keywords: (keywords && keywords.trim().length > 0) ? keywords : name, 
        sourceTable: tableName
    }));
};

